import { Container } from '@mui/material';
import './Formulario.css';
import { useState } from "react";

export function Formulario({ setUser }) {
    const [usuario, setUsuario] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState(false);
    const [isLogin, setIsLogin] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (usuario === "" || password === "") {
            setError(true);
            return;
        }
        setError(false);
        if (isLogin) {
            
            setUser(usuario);
        } else {
            
            console.log("Registrarse");
        }
    };

    const toggleForm = () => {
        setIsLogin(!isLogin);
    };

    return (
        <section> 
            <h1>{isLogin ? "Login" : "Registro"}</h1>

            <Container className="conten">
                <form className="formulario" onSubmit={handleSubmit}>
                    <label className="input-box label">Nombre de usuario</label>
                    <input className="input-box" placeholder='usuario'
                        type="text"
                        value={usuario}
                        onChange={(e) => setUsuario(e.target.value)}
                    />
                    <label className="input-box label">Contraseña</label>
                    <input className="input-box" placeholder='contraseña'
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button type="submit">{isLogin ? "Iniciar sesión" : "Registrarse"}</button>
                    <button type="button" onClick={toggleForm}>{isLogin ? "Registrarse" : "Volver al inicio de sesión"}</button>
                </form>
                {error && <p>Todos los campos tienen que estar llenos</p>}
            </Container>
        </section>
    );
}
